import React, { useState, useEffect, useRef } from 'react';
import './App.css'; // カスタムスタイルのために追加
import { BrowserRouter as Router, Route, Routes, Link } from 'react-router-dom'; // React Router のインポート

function Home({ openPack, cards, currentCardIndex, handleCardClick, packsOpenedToday, maxPacksPerDay }) {
  return (
    <div style={{ textAlign: 'center' }}>
      <h1>今日のカードパックを開けよう！（残り {maxPacksPerDay - packsOpenedToday} パック）</h1>
      <button onClick={openPack} disabled={packsOpenedToday >= maxPacksPerDay}>
        パックを開ける
      </button>
      {cards.length > 0 && (
        <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
          {cards.map((card, index) => (
            <div
              key={index}
              onClick={() => handleCardClick(index)}
              style={{
                width: currentCardIndex === index ? '300px' : '150px',
                height: currentCardIndex === index ? '300px' : '150px',
                objectFit: 'cover',
                marginLeft: index !== 0 ? '-50px' : '0',
                transition: 'transform 0.3s ease, width 0.3s ease, height 0.3s ease',
                cursor: 'pointer',
                zIndex: currentCardIndex === index ? 10 : 1,
                transform: currentCardIndex === index ? 'scale(1.1)' : 'scale(1)',
              }}
            >
              <img src={card.image} alt={card.name} style={{ width: '100%', height: '100%', objectFit: 'cover' }} />
            </div>
          ))}
        </div>
      )}
      <Link to="/cards">
        <button style={{ marginTop: '20px' }}>手に入れたカード一覧を見る</button>
      </Link>
    </div>
  );
}

function Cards({ ownedCards }) {
  return (
    <div style={{ textAlign: 'center' }}>
      <h1>手に入れたカード一覧</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
        {ownedCards.map((card, index) => (
          <div key={index} style={{ margin: '10px' }}>
            <img src={card.image} alt={card.name} style={{ width: '100px', height: '100px', objectFit: 'cover' }} />
            <p>{card.name} ({card.rarity})</p>
          </div>
        ))}
      </div>
      <Link to="/">
        <button style={{ marginTop: '20px' }}>戻る</button>
      </Link>
    </div>
  );
}

function App() {
  const [currentCardIndex, setCurrentCardIndex] = useState(null); // 拡大するカードのインデックスを保持
  const [cards, setCards] = useState([]);
  const [ownedCards, setOwnedCards] = useState([]); // 手に入れたカードのリスト
  const [packsOpenedToday, setPacksOpenedToday] = useState(0); // 今日開けたパック数
  const maxPacksPerDay = 2;

  // サウンドエフェクトを保持するための useRef
  const cardFlipSound = useRef(new Audio('/sounds/card-flip.mp3'));

  useEffect(() => {
    const savedData = localStorage.getItem('packsOpenedToday');
    const savedDate = localStorage.getItem('lastOpenedDate');
    const today = new Date().toDateString();

    if (savedDate === today) {
      setPacksOpenedToday(Number(savedData) || 0);
    } else {
      localStorage.setItem('lastOpenedDate', today);
      setPacksOpenedToday(0);
    }

    const savedCards = JSON.parse(localStorage.getItem('ownedCards')) || [];
    setOwnedCards(savedCards);
  }, []);

  const openPack = () => {
    if (packsOpenedToday >= maxPacksPerDay) {
      alert('今日はこれ以上パックを開けられません！');
      return;
    }

    const selectedCards = [];
    for (let i = 0; i < 5; i++) {
      const randomCard = availableCards[Math.floor(Math.random() * availableCards.length)];
      selectedCards.push(randomCard);
    }

    const newOwnedCards = [...ownedCards, ...selectedCards];
    setOwnedCards(newOwnedCards);
    localStorage.setItem('ownedCards', JSON.stringify(newOwnedCards));

    setCards(selectedCards);
    setCurrentCardIndex(null);
    const newPacksOpenedToday = packsOpenedToday + 1;
    setPacksOpenedToday(newPacksOpenedToday);
    localStorage.setItem('packsOpenedToday', newPacksOpenedToday);
  };

  const handleCardClick = (index) => {
    if (currentCardIndex === index) {
      setCurrentCardIndex(null); // 元に戻す
    } else {
      setCurrentCardIndex(index); // 拡大する
    }

    if (cardFlipSound.current) {
      cardFlipSound.current.play(); // SE再生
    }
  };

  const availableCards = Array.from({ length: 50 }, (v, i) => ({
    name: `カード${i + 1}`,
    rarity: (i + 1) % 3 === 0 ? "super-rare" : (i + 1) % 2 === 0 ? "rare" : "normal",
    image: `/images/${i + 1}.jpg`
  }));

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            <Home
              openPack={openPack}
              cards={cards}
              currentCardIndex={currentCardIndex}
              handleCardClick={handleCardClick}
              packsOpenedToday={packsOpenedToday}
              maxPacksPerDay={maxPacksPerDay}
            />
          }
        />
        <Route path="/cards" element={<Cards ownedCards={ownedCards} />} />
      </Routes>
    </Router>
  );
}

export default App;
