import React, { useState } from 'react';
import './App.css'; // カスタムスタイルのために追加

function App() {
  const [cards, setCards] = useState([]);
  const [isPackOpened, setIsPackOpened] = useState(false); // パックが開かれたかどうか

  // 1.jpg から 50.jpg までの画像を動的に生成
  const availableCards = Array.from({ length: 50 }, (v, i) => ({
    name: `カード${i + 1}`,
    rarity: (i + 1) % 3 === 0 ? "super-rare" : (i + 1) % 2 === 0 ? "rare" : "normal",
    image: `/images/${i + 1}.jpg`
  }));

  const openPack = () => {
    const selectedCards = [];
    for (let i = 0; i < 5; i++) {
      const randomCard = availableCards[Math.floor(Math.random() * availableCards.length)];
      selectedCards.push(randomCard);
    }
    setCards(selectedCards);
    setIsPackOpened(true); // パックを開く
  };

  return (
    <div style={{ textAlign: 'center' }}>
      {!isPackOpened ? (
        <div onClick={openPack} style={{ cursor: 'pointer', padding: '50px', border: '2px solid black' }}>
          <h1>パックを開ける</h1>
          <img src="/images/pack.png" alt="パック" style={{ width: '200px', height: 'auto' }} /> {/* パックの画像 */}
        </div>
      ) : (
        <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
          {cards.map((card, index) => (
            <div
              key={index}
              style={{
                opacity: 0,
                animation: `fadeIn 0.5s ease-in-out ${index * 0.5}s forwards`, // 順番にカードが表示される
                marginLeft: index !== 0 ? '-50px' : '0',
              }}
            >
              <img
                src={card.image}
                alt={card.name}
                style={{
                  width: '150px',
                  height: '150px',
                  objectFit: 'cover',
                  transition: 'transform 0.3s ease',
                }}
              />
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
