import React, { useState } from 'react';
import './App.css'; // カスタムスタイルのために追加

function App() {
  const [currentCardIndex, setCurrentCardIndex] = useState(0);
  const [cards, setCards] = useState([]);

  // 1.jpg から 50.jpg までの画像を動的に生成
  const availableCards = Array.from({ length: 50 }, (v, i) => ({
    name: `カード${i + 1}`,
    rarity: (i + 1) % 3 === 0 ? "super-rare" : (i + 1) % 2 === 0 ? "rare" : "normal",
    image: `/images/${i + 1}.jpg`
  }));

  const openPack = () => {
    // 50枚の中からランダムに5枚のカードを選ぶ
    const selectedCards = [];
    for (let i = 0; i < 5; i++) {
      const randomCard = availableCards[Math.floor(Math.random() * availableCards.length)];
      selectedCards.push(randomCard);
    }
    setCards(selectedCards);
    setCurrentCardIndex(0); // 最初のカードを表示する
  };

  const nextCard = () => {
    if (currentCardIndex < cards.length - 1) {
      setCurrentCardIndex(currentCardIndex + 1);
    }
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>今日のカードパックを開けよう！</h1>
      <button onClick={openPack}>パックを開ける</button>

      {cards.length > 0 && (
        <div onClick={nextCard} style={{ position: 'relative', display: 'inline-block', cursor: 'pointer' }}>
          <img
            src={cards[currentCardIndex].image}
            alt={cards[currentCardIndex].name}
            style={{
              width: '200px',
              height: '300px',
              boxShadow: '0 4px 8px rgba(0, 0, 0, 0.2)',
              position: 'absolute',
              top: 0,
              left: 0,
              zIndex: cards.length - currentCardIndex
            }}
          />
        </div>
      )}
    </div>
  );
}

export default App;
