function Home({ openWafer, sticker, handleStickerClick, packsOpenedToday, maxPacksPerDay }) {
  const [isWaferOpened, setIsWaferOpened] = useState(false); 
  const [showSticker, setShowSticker] = useState(false); 

  const handleOpenWafer = () => {
    setIsWaferOpened(true);
    console.log("isWaferOpened:", isWaferOpened);  // ウエハースが開いたか確認
    setTimeout(() => {
      setShowSticker(true);
      console.log("showSticker:", showSticker);    // シールが表示されているか確認
      openWafer(); 
    }, 1000);
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>今日のウエハースを開けよう！（残り {Math.max(maxPacksPerDay - packsOpenedToday, 0)} パック）</h1>
      <button onClick={handleOpenWafer} disabled={packsOpenedToday >= maxPacksPerDay}>
        ウエハースを開ける
      </button>

      <div className={`wafer ${isWaferOpened ? 'open' : ''}`} style={{ margin: '20px auto', perspective: '1000px' }}>
        <img src="/images/IMG_1159.jpg" alt="ウエハース" style={{ width: '200px', height: '200px' }} />
      </div>

      {showSticker && sticker && (
        <div onClick={handleStickerClick} className={`sticker`} style={{ marginTop: '20px' }}>
          <img src={sticker.image} alt={sticker.name} style={{ width: '200px', height: '200px', objectFit: 'cover' }} />
        </div>
      )}

      <Link to="/stickers">
        <button style={{ marginTop: '20px' }}>手に入れたシール一覧を見る</button>
      </Link>
    </div>
  );
}
function convertRarityToStars(rarity) {
  const stars = {
    "normal": "★",
    "rare": "★★",
    "super-rare": "★★★"
  };
  return stars[rarity] || "";
}
function Stickers({ ownedStickers }) {
  return (
    <div style={{ textAlign: 'center' }}>
      <h1>手に入れたシール一覧</h1>
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
        {ownedStickers.map((sticker, index) => (
          <div key={index} style={{ margin: '10px' }}>
            <img src={sticker.image} alt={sticker.name} style={{ width: '100px', height: '100px', objectFit: 'cover' }} />
            <p>{sticker.name} ({convertRarityToStars(sticker.rarity)}) ×{sticker.count}</p>
          </div>
        ))}
      </div>
      <Link to="/">
        <button style={{ marginTop: '20px' }}>戻る</button>
      </Link>
    </div>
  );
}
