import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from 'react-router-dom';

// Stickersコンポーネントの定義
function Stickers({ ownedStickers, collectionRate }) {
  return (
    <div style={{ textAlign: 'center' }}>
      <h1>手に入れたシール一覧</h1>
      <p>コレクション率: {collectionRate}%</p> {/* コレクション率を表示 */}
      <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center' }}>
        {ownedStickers.map((sticker, index) => (
          <div key={index} style={{ margin: '10px' }}>
            <img src={sticker.image} alt={sticker.name} style={{ width: '100px', height: '100px', objectFit: 'cover' }} />
            <p>{sticker.name} ({sticker.rarity}) ×{sticker.count}</p>
          </div>
        ))}
      </div>
      <Link to="/">
        <button style={{ marginTop: '20px' }}>戻る</button>
      </Link>
    </div>
  );
}

// Homeコンポーネントの定義
function Home({ openWafer, sticker, handleStickerClick, packsOpenedToday, maxPacksPerDay }) {
  const [isWaferOpened, setIsWaferOpened] = useState(false);
  const [showSticker, setShowSticker] = useState(false);

  const waferOpenSound = useRef(new Audio('/sounds/wafer-open.mp3'));
  const stickerRevealSound = useRef(new Audio('/sounds/sticker-reveal.mp3')); // シールが出現する時のSE
  const viewStickersSound = useRef(new Audio('/sounds/view-stickers.mp3'));

  const handleOpenWafer = () => {
    if (waferOpenSound.current) {
      waferOpenSound.current.play();
    }

    setIsWaferOpened(true);
    setTimeout(() => {
      if (stickerRevealSound.current) {
        stickerRevealSound.current.play(); // シールが出る時のサウンドを再生
      }
      setShowSticker(true);
      openWafer();
    }, 1000);
  };

  const navigate = useNavigate();
  const handleViewStickersClick = () => {
    if (viewStickersSound.current) {
      viewStickersSound.current.play();
    }

    setTimeout(() => {
      navigate('/stickers');
    }, 500);
  };

  return (
    <div style={{ textAlign: 'center', position: 'relative', height: '800px' }}>
      <h1>今日のウエハースを開けよう！（残り {Math.max(maxPacksPerDay - packsOpenedToday, 0)} パック）</h1>
      <button onClick={handleOpenWafer} disabled={packsOpenedToday >= maxPacksPerDay}>
        ウエハースを開ける
      </button>

      {/* ウエハースの画像表示 */}
      <div className={`wafer ${isWaferOpened ? 'open' : ''}`} 
           style={{ margin: '50px auto', perspective: '1000px' }}>
        <img src={`${process.env.PUBLIC_URL}/images/1159.jpg`} alt="ウエハース" 
             style={{ width: 'auto', height: 'auto', maxWidth: '500px', maxHeight: '500px' }} />
      </div>

      {showSticker && sticker && (
        <div onClick={handleStickerClick} 
             className={`sticker ${showSticker ? 'show' : ''}`} 
             style={{ position: 'absolute', bottom: '300px', left: '30%', transform: 'translateX(-50%)' }}>
          <img src={sticker.image} alt={sticker.name} style={{ width: '300px', height: 'auto', objectFit: 'cover' }} />
          <p style={{ textAlign: 'center' }}>{sticker.rarity}</p> {/* レアリティ（★）を表示 */}
        </div>
      )}

      <button 
        style={{ 
          marginTop: '20px', 
          position: 'absolute', 
          bottom: '50px',  
          left: '50%', 
          transform: 'translateX(-50%)' 
        }} 
        onClick={handleViewStickersClick}
      >
        手に入れたシール一覧を見る
      </button>
    </div>
  );
}

// Appコンポーネントの定義
function App() {
  const [sticker, setSticker] = useState(null); 
  const [ownedStickers, setOwnedStickers] = useState([]); 
  const [packsOpenedToday, setPacksOpenedToday] = useState(0); 
  const maxPacksPerDay = 2;

  const stickerFlipSound = useRef(new Audio('/sounds/card-flip.mp3'));

  useEffect(() => {
    const savedStickers = JSON.parse(localStorage.getItem('ownedStickers')) || [];
    setOwnedStickers(savedStickers);
  }, []);

  const openWafer = () => {
    const randomValue = Math.random() * 100;
    let rarity = "★"; // 初期値は★

    if (randomValue < 5) {
      rarity = "★★★";  // 5%の確率で★★★
    } else if (randomValue < 30) {
      rarity = "★★";  // 25%の確率で★★
    } 

    const randomSticker = availableStickers.find(s => s.rarity === rarity);

    const newOwnedStickers = [...ownedStickers];
    const existingSticker = newOwnedStickers.find(s => s.name === randomSticker.name);
    if (existingSticker) {
      existingSticker.count += 1; 
    } else {
      newOwnedStickers.push({ ...randomSticker, count: 1 }); 
    }

    setOwnedStickers(newOwnedStickers);
    localStorage.setItem('ownedStickers', JSON.stringify(newOwnedStickers));

    setSticker(randomSticker);
    const newPacksOpenedToday = packsOpenedToday + 1;
    setPacksOpenedToday(newPacksOpenedToday);
    localStorage.setItem('packsOpenedToday', newPacksOpenedToday);
  };

  const handleStickerClick = () => {
    if (stickerFlipSound.current) {
      stickerFlipSound.current.play();
    }
    setSticker(null); 
  };

  const availableStickers = Array.from({ length: 50 }, (v, i) => ({
    name: `シール${i + 1}`,
    rarity: (i + 1) % 3 === 0 ? "★★★" : (i + 1) % 2 === 0 ? "★★" : "★",
    image: `/images/${i + 1}.jpg`
  }));

  // コレクション率を計算
  const collectionRate = Math.round((ownedStickers.length / availableStickers.length) * 100);

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={
            <Home
              openWafer={openWafer}
              sticker={sticker}
              handleStickerClick={handleStickerClick}
              packsOpenedToday={packsOpenedToday}
              maxPacksPerDay={maxPacksPerDay}
            />
          }
        />
        <Route path="/stickers" element={<Stickers ownedStickers={ownedStickers} collectionRate={collectionRate} />} />
      </Routes>
    </Router>
  );
}

export default App;
