import React, { useState } from 'react';

function App() {
  const [cards, setCards] = useState([]);

  const openPack = () => {
    const availableCards = [
      { name: "レアカード", rarity: "rare" },
      { name: "ノーマルカード", rarity: "normal" },
      { name: "スーパーレア", rarity: "super-rare" }
    ];
    // 3枚のカードをランダムに選ぶ
    const selectedCards = [];
    for (let i = 0; i < 3; i++) {
      const randomCard = availableCards[Math.floor(Math.random() * availableCards.length)];
      selectedCards.push(randomCard);
    }
    setCards(selectedCards);
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>今日のカードパックを開けよう！</h1>
      <button onClick={openPack}>パックを開ける</button>
      <div>
        {cards.length > 0 && (
          <div>
            <h2>出てきたカード:</h2>
            <ul>
              {cards.map((card, index) => (
                <li key={index}>{card.name} - {card.rarity}</li>
              ))}
            </ul>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
