import React, { useState } from 'react';
import './App.css'; // カスタムスタイルのために追加

function App() {
  const [currentCardIndex, setCurrentCardIndex] = useState(null); // 拡大するカードのインデックスを保持
  const [cards, setCards] = useState([]);

  // 1.jpg から 50.jpg までの画像を動的に生成
  const availableCards = Array.from({ length: 50 }, (v, i) => ({
    name: `カード${i + 1}`,
    rarity: (i + 1) % 3 === 0 ? "super-rare" : (i + 1) % 2 === 0 ? "rare" : "normal",
    image: `/images/${i + 1}.jpg`
  }));

  const openPack = () => {
    // 50枚の中からランダムに5枚のカードを選ぶ
    const selectedCards = [];
    for (let i = 0; i < 5; i++) {
      const randomCard = availableCards[Math.floor(Math.random() * availableCards.length)];
      selectedCards.push(randomCard);
    }
    setCards(selectedCards);
    setCurrentCardIndex(null); // リセットして拡大表示を解除
  };

  const handleInteraction = (index) => {
    // クリックまたはタッチ時にカードを拡大
    setCurrentCardIndex(index);
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>今日のカードパックを開けよう！</h1>
      <button onClick={openPack}>パックを開ける</button>

      {cards.length > 0 && (
        <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
          {cards.map((card, index) => (
            <div
              key={index}
              onClick={() => handleInteraction(index)} // クリックイベント
              onTouchStart={() => handleInteraction(index)} // タッチイベント
              style={{
                width: currentCardIndex === index ? '300px' : '150px',  // 拡大時は300px、通常時は150px
                height: currentCardIndex === index ? '300px' : '150px',
                objectFit: 'cover',
                marginLeft: index !== 0 ? '-50px' : '0',
                transition: 'transform 0.3s ease, width 0.3s ease, height 0.3s ease',
                cursor: 'pointer',
                zIndex: currentCardIndex === index ? 10 : 1,
                transform: currentCardIndex === index ? 'scale(1.1)' : 'scale(1)'
              }}
            >
              <img
                src={card.image}
                alt={card.name}
                style={{
                  width: '100%',
                  height: '100%',
                  objectFit: 'cover',
                }}
              />
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default App;
